let currentIndex = 0;

function showCertificate(index) {
    const imgElement = document.getElementById('certificate-image');
    imgElement.src = certificates[index];
    updateLabel(index);
    updateButtons();
}

function updateLabel(index) {
    const labelElement = document.getElementById('certificate-label');
    labelElement.textContent = `${index + 1} / ${certificates.length}`;
}

document.getElementById('prev-btn').addEventListener('click', () => {
    if (currentIndex > 0) {
        currentIndex--;
        showCertificate(currentIndex);
    }
});

document.getElementById('next-btn').addEventListener('click', () => {
    if (currentIndex < certificates.length - 1) {
        currentIndex++;
        showCertificate(currentIndex);
    }
});

